import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';

import axios from 'utils/axios';
import { CustomerInfo, Invoices, SendEmails, OtherActions } from './components';
import {useParams } from 'react-router-dom';

import { FETCH_USERS } from '../../../../graphql/queries/users'
import  {fetchAccessLevels } from 'store/access-level-actions';



const useStyles = makeStyles(() => ({
  root: {}
}));

const Summary = props => {
  const { className, ...rest } = props;
  const [user, setUsers] = useState([]);


  const classes = useStyles();
  const [customer, setCustomer] = useState();
  let { id } = useParams();

  const userId = id;
  console.log( ' The params ============> 3333', userId); // 👉️ {userId: '4200'}



  const fetchUsers = async(selectedUserId) =>{
    console.log( ' The params ============> 44', selectedUserId)

    await fetchAccessLevels(FETCH_USERS,selectedUserId).then(user =>{
      console.log( ' The params ============> 55', user.data.getUsers)
      setUsers(user.data.getUsers[0]);
    });//

  }




  useEffect(() => {
    let mounted = true;






    const fetchCustomer = () => {
      axios.get('/api/management/customers/1/summary').then(response => {
        if (mounted) {
          setCustomer(response.data.summary);
          fetchUsers(id); 
        }
      });
    };
    fetchCustomer()
    fetchUsers(id);  

    return () => {
      mounted = false;
    };
  }, []);

  if (!customer) {
    return null;
  }

  return (
    <Grid
      {...rest}
      className={clsx(classes.root, className)}
      container
      spacing={3}
    >
      <Grid
        item
        lg={4}
        md={6}
        xl={3}
        xs={12}
      >
        <CustomerInfo user={user} />
      </Grid>
      <Grid
        item
        lg={4}
        md={6}
        xl={3}
        xs={12}
      >
        <Invoices customer={customer} />
      </Grid>
      <Grid
        item
        lg={4}
        md={6}
        xl={3}
        xs={12}
      >
        <SendEmails customer={customer} />
      </Grid>
      <Grid
        item
        lg={4}
        md={6}
        xl={3}
        xs={12}
      >
        <OtherActions />
      </Grid>
    </Grid>
  );
};

Summary.propTypes = {
  className: PropTypes.string
};

export default Summary;
