import React, { useState } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import Autocomplete from '@mui/material/Autocomplete';

import {
  Modal,
  Card,
  CardContent,
  CardActions,
  Grid,
  Typography,
  TextField,
  Switch,
  Button,
  colors
} from '@material-ui/core';
import { boolean } from 'yup/lib/locale';

const useStyles = makeStyles(theme => ({
  root: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    outline: 'none',
    boxShadow: theme.shadows[20],
    width: 700,
    maxHeight: '100%',
    overflowY: 'auto',
    maxWidth: '100%'
  },
  container: {
    marginTop: theme.spacing(3)
  },
  actions: {
    justifyContent: 'flex-end'
  },
  saveButton: {
    color: theme.palette.white,
    backgroundColor: colors.green[600],
    '&:hover': {
      backgroundColor: colors.green[900]
    }
  }
}));

const EnrollInProgram = props => {
  const { open, onClose, user, programs,save, className, ...rest } = props;

  const classes = useStyles();
  const enrollProgram = {
    userId: '',
    enrollStatus: false,
    programId: '',
  }

  const [formState, setFormState] = useState(
    enrollProgram
  );

  if (!open) {
    return null;
  }



  const handleFieldChange = event => {
    event.persist();
    setFormState(formState => ({
      ...formState,
      [event.target.name]:
        event.target.type === 'checkbox'
          ? event.target.checked
          : event.target.value
    }));
  };

  console.log("user user user user ",formState)

  return (
    <Modal
      onClose={onClose}
      open={open}
    >
      <Card
        {...rest}
        className={clsx(classes.root, className)}
      >
        <form>
          <CardContent>
            <Typography
              align="center"
              gutterBottom
              variant="h3"
            >
              Enroll Client
            </Typography>
            <Grid
              className={classes.container}
              container
              spacing={3}
            >
              <Grid
                item
                md={12}
                xs={12}
              >
                <TextField
                  disabled
                  fullWidth
                  label="Surname"
                  name="email"
                  onChange={handleFieldChange}
                  value={user.surname} 
                  variant="outlined"
                />
              </Grid>
              <Grid
                item
                md={12}
                xs={12}
              >
                <Autocomplete
                  className={classes.field}
                  fullWidth

                  id="combo-box-demo"
                  options={programs}
                  getOptionLabel={programs => programs.programName}
                  renderInput={(params) => <TextField {...params} label="Select Program"   variant="outlined"/>}
                  onChange={(event, newValue) => {
                    enrollProgram.programId = newValue.id;
                    enrollProgram.userId = user._id

                    console.log('At auto programs',enrollProgram);


                  // setTypeOptions(newValue);
                  }}

                />
              </Grid>

              <Grid
                item
                md={6}
                xs={12}
              >
                <Typography variant="h5">Consent</Typography>
                <Typography variant="body2">
                  The client consented to draw blood samples
                </Typography>
                <Switch
                  checked={formState.enrollStatus}
                  color="secondary"
                  edge="start"
                  name="enrollStatus"
                  onChange={handleFieldChange}
                  value={formState.enrollStatus}
                />
              </Grid>
            </Grid>
          </CardContent>
          <CardActions className={classes.actions}>
            <Button
              onClick={onClose}
              variant="contained"
            >
              Close
            </Button>
            <Button
              className={classes.saveButton}
              onClick={() => {
                save()
              }}
              variant="contained"
            >
              Save
            </Button>
          </CardActions>
        </form>
      </Card>
    </Modal>
  );
};

EnrollInProgram.displayName = 'Enroll In Program';

EnrollInProgram.propTypes = {
  className: PropTypes.string,
  customer: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool
};

EnrollInProgram.defaultProps = {
  open: false,
  onClose: () => {}
};

export default EnrollInProgram;
