import { makeStyles } from '@material-ui/styles';
import { Page, SearchBar } from 'components';
import axios from 'utils/axios';

import { FETCH_USERS } from '../../graphql/queries/users'
import  {fetchAccessLevels } from 'store/api-actions';





import { Header,ListUser } from './components'
import { async } from 'validate.js';

const useStyles = makeStyles(theme => ({
  root: {
    padding: theme.spacing(3)
  },
  results: {
    marginTop: theme.spacing(3)
  }
}));


function ListItems() {




  const classes = useStyles();
  const handleFilter = () => {};
  const handleSearch = () => {};


  const [customers, setCustomers] = useState([]);
  const [users, setUsers] = useState([]);
  const [data1, setdata] = useState([]);

  const fetchUsers = async() =>{
    await fetchAccessLevels(FETCH_USERS).then(user =>{
      setUsers(user.data.getUsers);
    });//

  }



  useEffect(() => {

    fetchUsers();    

  }, []);





  return (
    <Page
      className={classes.root}
      title="List of all Users"
    >
      {/*  <Header /> */}
      <SearchBar
        onSearch={handleSearch}
      />
      { users && (
        <ListUser
          className={classes.results}
          users={users}
        />
      )} 

    </Page>
  )
}

export default ListItems
