import React, { useEffect, useState } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import Autocomplete from '@mui/material/Autocomplete';

//import { userActions} from '../../../../store/user-slice'
//import { saveUsers, fetchUsers } from '../../../../store/user-actions'
//import { useSelector, useDispatch} from 'react-redux';


//import { CREATE_USER, REGISTER_USER2 } from '../../../../graphql/mutations/users';

import {  FETCH_DESIGNATIONS } from '../../../../graphql/queries/access-level-query';

import  {fetchAccessLevels, saveAccessLevel } from 'store/access-level-actions';




import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardHeader,
  Grid,
  Divider,
  TextField,
  colors
} from '@material-ui/core';

import SuccessSnackbar from '../SuccessSnackbar';

const useStyles = makeStyles(theme => ({
  root: {},
  saveButton: {
    color: theme.palette.white,
    backgroundColor: colors.green[600],
    '&:hover': {
      backgroundColor: colors.green[900]
    }
  }
}));

const Users = props => {
  const { className, ...rest } = props;

  const classes = useStyles();
  const [openSnackbar, setOpenSnackbar] = useState(false);

  const [designations, setDesignations] = useState([]);
  const [selectedDesignation, setSelectedDesignation] = useState([]);

  const [userDetails, setUserDetails] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    email: '',
    phone: '',
    gender:'',
    documentNumber: '',
    organization:'',
    role:'',
    username:'',
    password:'',

    isPublic: '',
    canHire: '',
  });
  //const user = useSelector((state) => state.user);
 // const dispatch = useDispatch();


  // eslint-disable-next-line no-unused-vars
  //const [addUsers, { data, loading, error }] = useMutation(CREATE_USER);

  //const [addUsers, { data, loading, error }] = useMutation(REGISTER_USER2);



  const fetchDesignations = async () => {
    await fetchAccessLevels(FETCH_DESIGNATIONS).then(res =>{  
      console.log('Designations fetched ',res.data.getAllDesignations)
      setDesignations(res.data.getAllDesignations) 

    })
  }

  useEffect(() =>{
    fetchDesignations();
  },[])


  /*  const saveResData = async(event) => {
    await saveAccessLevel(event,CREATE_DESIGNATIONS).then(res =>{
      setAccessLevel([...designations, res.data.createDesignations]);
    });
  };
 */




  const handleChange = event => {
    event.persist();

    setUserDetails({
      ...userDetails,
      [event.target.name]:
        event.target.type === 'checkbox'
          ? event.target.checked
          : event.target.value
    });
  };
  //const userValues = { username:'agik11', email:'agikedwin@gmail11.com', password:'agik11', confirmPassword:'agik11' };


  const handleSubmit = event => {
    event.preventDefault();
    setOpenSnackbar(true);

    //addUsers({ variables: userValues });
  };

  const handleSnackbarClose = () => {
    setOpenSnackbar(false);
  };

  const genderTypes = ['Male', 'Female'];
  const roles = ['Admin','User','Loanee','Others']


  return (
    <Card
      {...rest}
      className={clsx(classes.root, className)}
    >
      <form onSubmit={handleSubmit}>
        <CardHeader title="user" />
        <Divider />
        <CardContent>
          <Grid
            container
            spacing={1}
          >
            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                helperText="Please specify the first name"
                label="First name"
                name="firstName"
                onChange={handleChange}
                required
                value={userDetails.firstName}
                variant="outlined"
              />
            </Grid>
            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                label="Middle name"
                name="middleName"
                onChange={handleChange}
                required
                value={userDetails.middleName}
                variant="outlined"
              />
            </Grid>

            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                label="Last name"
                name="lastName"
                onChange={handleChange}
                required
                value={userDetails.lastName}
                variant="outlined"
              />
            </Grid>
            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                label="Email Address"
                name="email"
                onChange={handleChange}
                required
                value={userDetails.email}
                variant="outlined"
              />
            </Grid>
            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                label="Phone Number"
                name="phone"
                onChange={handleChange}
                type="text"
                value={userDetails.phone}
                variant="outlined"
              />
            </Grid>
            <Grid
              item
              md={12}
              xs={12}
            >
              <Autocomplete
                className={classes.field}
                fullWidth
                id="combo-box-demo"
                options={designations}
                getOptionLabel={options => options.designation_name}
                renderInput={(params) => <TextField {...params} label="Designation"   variant="outlined"/>}
                onChange={(event, newValue) => {
                  console.log(newValue);
                  setSelectedDesignation(newValue);
                }}

              />

            </Grid>
            <Grid
              item
              md={12}
              xs={12}
            >
              <Autocomplete
                className={classes.field}
                fullWidth
                id="combo-box-demo"
                options={designations}
                getOptionLabel={genderTypes => genderTypes}
                renderInput={(params) => <TextField {...params} label="Designation"   variant="outlined"/>}
                onChange={(event, newValue) => {
                  console.log(newValue);
                  setSelectedDesignation(newValue);
                }}

              />
            </Grid>

            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                label="User Name"
                name="username"
                onChange={handleChange}
                required
                value={userDetails.username}
                variant="outlined"
              />
            </Grid>

            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                label="Select Role"
                name="role"
                onChange={handleChange}
                select
                // eslint-disable-next-line react/jsx-sort-props
                SelectProps={{ native: true }}
                value={userDetails.role}
                variant="outlined"
              >
                {roles.map(role => (
                  <option
                    key={role}
                    value={role}
                  >
                    {role}
                  </option>
                ))}
              </TextField>
            </Grid>
            <Grid
              item
              md={12}
              xs={12}
            >
              <TextField
                fullWidth
                label="Password"
                name="password"
                onChange={handleChange}
                type="text"
                value={userDetails.password}
                variant="outlined"
              />
            </Grid>


          </Grid>
        </CardContent>
        <Divider />
        <CardActions>
          <Button
            className={classes.saveButton}
            type="submit"
            variant="contained"
          >
            Save Changes
          </Button>
        </CardActions>
      </form>
      <SuccessSnackbar
        onClose={handleSnackbarClose}
        open={openSnackbar}
      />
    </Card>
  );
};

Users.propTypes = {
  className: PropTypes.string,
  //user: PropTypes.object.isRequired
};


export default Users
