import React, { useState } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import {
  Modal,
  Card,
  CardContent,
  CardActions,
  Grid,
  Typography,
  TextField,
  Switch,
  Button,
  colors,

} from '@material-ui/core';
import Autocomplete from '@mui/material/Autocomplete';
import moment from 'moment';
import { DatePicker } from '@material-ui/pickers';
import { alpha, color } from '@material-ui/core/styles';




const useStyles = makeStyles(theme => ({
  root: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    outline: 'none',
    boxShadow: theme.shadows[20],
    width: 700,
    maxHeight: '100%',
    overflowY: 'auto',
    maxWidth: '100%'
  },
  container: {
    marginTop: theme.spacing(3)
  },
  actions: {
    justifyContent: 'flex-end'
  },
  saveButton: {
    alpha: theme.palette.white,
    backgroundColor: colors.green[600],
    '&:hover': {
      backgroundColor: colors.green[900]
    }
  }
}));

const AddServices = props => {
  const classes = useStyles();
  const { open, onClose, visit, visitServices,className, ...rest } = props;




  const [calendarTrigger, setCalendarTrigger] = useState(null);
  const calendarOpen = Boolean(calendarTrigger);
  const [calendaControl, setCalendaControl] = useState('');


  const [formState, setFormState] = useState({

    visitName: visit,
    visitType: [],
    visitDate: '',
    serviceOffered: [],
    nextVisitdate: '',
  });

  const [typeVistOptions, setTypeOptions] = useState();



  const handleCalendarOpen = (trigger,contromName) => {
    setCalendaControl(contromName)
    console.log(trigger, '   =====   Calendar::: ==== ',name)
    setCalendarTrigger(trigger);
  };

  const handleCalendarChange = () => {};

  const handleCalendarAccept = date => {  
    console.log('Calendar::: ',formState)
    calendaControl ==='visitDate' ?   formState.visitDate = date._d : formState.nextVisitdate = date._d
    formState.visitDate = date._d
    console.log('Calendar::: ',formState)

  };

  const handleCalendarAcceptNext = date => {    
    formState.nextVisitdate = date._d
    console.log('Calendar::: ',date)

  };

  const handleCalendarClose = () => {
    setCalendarTrigger(false);
  };


  console.log('THE VISIT STATE::: ',formState)







  if (!open) {
    return null;
  }

  const handleFieldChange = event => {
    event.persist();
    setFormState(formState => ({
      ...formState,
      [event.target.name]:
        event.target.type === 'checkbox'
          ? event.target.checked
          : event.target.value
    }));
  };

  const visitTypesList = [{
    typeId: 1,
    typeName: 'Physical Visit',
    active: true,
  },
  {
    typeId: 2,
    typeName: 'Phone Call Visit',
    active: true,

  }

  ]


  return (

    <Modal
      onClose={onClose}
      open={open}
    >

      <Card
        {...rest}
        className={clsx(classes.root, className)}
      >
        <form>
          <CardContent>
            <Typography
              align="center"
              gutterBottom
              variant="h3"
            >
             Client Visits
            </Typography>
            <Grid
              className={classes.container}
              container
              spacing={3}
            >
              <Grid
                item
                md={12}
                xs={12}
              >
                <TextField
                  fullWidth
                  disabled
                  label="Visit Number"
                  name="visitNumber"
                  onChange={handleFieldChange}
                  value={visit.visitNumber}
                  variant="outlined"
                />
              </Grid>
              <Grid
                item
                md={12}
                xs={12}
              >
                <TextField
                  fullWidth
                  disabled
                  label="Visit Name"
                  name="visitName"
                  onChange={handleFieldChange}
                  value={visit.visitName}
                  variant="outlined"
                />
              </Grid>
              <Grid
                item
                md={12}
                xs={12}
              >
                <TextField
                  className={classes.dateField}
                  fullWidth
                  label="Visit Date"
                  name="visitDate"
                  onClick={() => handleCalendarOpen('visitDate','visitDate')}
                  value={moment(formState.visitDate).format('DD/MM/YYYY')}
                  variant="outlined"
                />
              </Grid>
              <Grid
                item
                md={12}
                xs={12}
              >
                <Autocomplete
                  className={classes.field}
                  fullWidth

                  id="combo-box-demo"
                  options={visitTypesList}
                  getOptionLabel={visitTypesList => visitTypesList.typeName}
                  renderInput={(params) => <TextField {...params} label="Visit Type"   variant="outlined"/>}
                  onChange={(event, newValue) => {
                    formState.visitType = newValue
                    console.log('At auto',formState);


                    setTypeOptions(newValue);
                  }}

                />
              </Grid>

              <Grid
                item
                md={12}
                xs={12}
              >
                <Autocomplete
                  className={classes.field}
                  fullWidth

                  id="combo-box-demo"
                  options={visitServices}
                  getOptionLabel={visitServices => visitServices.serviceName}
                  renderInput={(params) => <TextField {...params} label="Service Being Offered"   variant="outlined"/>}
                  onChange={(event, newValue) => {
                    formState.serviceOffered = newValue
                    console.log('At auto',formState);


                    setTypeOptions(newValue);
                  }}

                />
              </Grid>

              <Grid
                item
                md={12}
                xs={12}
              >
                <TextField
                  className={classes.dateField}
                  fullWidth
                  label="Next Visit Date"
                  name="nextVisitDate"
                  onClick={() => handleCalendarOpen('nextVisitDate', 'nextVisitDate')}
                  value={moment(formState.nextVisitDate).format('DD/MM/YYYY')}
                  variant="outlined"
                />
              </Grid>


              <Grid
                item
                md={6}
                xs={12}
              >
                <Typography variant="h5">Consent</Typography>
                <Typography variant="body2">
                  The client consented to draw blood samples
                </Typography>
                <Switch
                  checked={formState.discountedPrices}
                  color="secondary"
                  edge="start"
                  name="discountedPrices"
                  onChange={handleFieldChange}
                  value={formState.discountedPrices}
                />
              </Grid>
            </Grid>
          </CardContent>
          <CardActions className={classes.actions}>
            <Button
              onClick={onClose}
              variant="contained"
            >
              Close
            </Button>
            <Button
              className={classes.saveButton}
              onClick={onClose}
              variant="contained"
            >
              Save
            </Button>
          </CardActions>
        </form> 

        <DatePicker
          onAccept={handleCalendarAccept}
          onChange={handleCalendarChange}
          onClose={handleCalendarClose}
          open={calendarOpen}
          style={{ display: 'none' }} // Temporal fix to hide the input element

          variant="dialog"
        />
      </Card>
    </Modal>
  );
};

AddServices.displayName = 'Add Services';

AddServices.propTypes = {
  className: PropTypes.string,
  //customer: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool
};

AddServices.defaultProps = {
  open: false,
  onClose: () => {}
};

export default AddServices;
