import React, { useState } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import moment from 'moment';
import { makeStyles } from '@material-ui/styles';
import {
  Avatar,
  Checkbox,
  IconButton,
  ListItem,
  Tooltip,
  Typography
} from '@material-ui/core';
import { colors } from '@material-ui/core';
import StarIcon from '@material-ui/icons/Star';
import StarBorderIcon from '@material-ui/icons/StarBorder';

import getInitials from 'utils/getInitials';
import { Label } from 'components';

const useStyles = makeStyles(theme => ({
  root: {
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    '&:hover': {
      backgroundColor: colors.grey[50]
    }
  },
  new: {
    position: 'relative',
    '&:before': {
      content: '" "',
      height: '100%',
      position: 'absolute',
      left: 0,
      top: 0,
      width: 4,
      backgroundColor: theme.palette.error.main
    },
    '& $name, & $subject': {
      fontWeight: theme.typography.fontWeightBold
    }
  },
  selected: {
    backgroundColor: colors.grey[50]
  },
  checkbox: {
    marginRight: theme.spacing(1),
    [theme.breakpoints.down('sm')]: {
      display: 'none'
    }
  },
  favoriteButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.down('sm')]: {
      display: 'none'
    }
  },
  starIcon: {
    cursor: 'pointer'
  },
  starFilledIcon: {
    color: colors.amber[400]
  },
  details: {
    minWidth: 1,
    display: 'flex',
    flexGrow: 1
  },
  avatar: {
    marginRight: theme.spacing(2)
  },
  name: {
    whiteSpace: 'nowrap',
    [theme.breakpoints.up('md')]: {
      minWidth: 180,
      flexBasis: 180
    }
  },
  content: {
    minWidth: 1,
    [theme.breakpoints.up('md')]: {
      display: 'flex',
      alignItems: 'center',
      flexGrow: 1
    }
  },
  subject: {
    maxWidth: 400,
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis'
  },
  separator: {
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(2),
    [theme.breakpoints.down('sm')]: {
      display: 'none'
    }
  },
  message: {
    maxWidth: 800,
    flexGrow: 1,
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    marginRight: 'auto',
    [theme.breakpoints.down('sm')]: {
      display: 'none'
    }
  },
  labels: {
    display: 'flex',
    marginRight: theme.spacing(2),
    '& > * + *': {
      marginLeft: theme.spacing(1)
    },
    [theme.breakpoints.down('sm')]: {
      display: 'none'
    }
  },
  date: {
    whiteSpace: 'nowrap'
  }
}));

const EmailItem = props => {
  const { email, selected, onOpen, onToggle, className, ...rest } = props;

  const classes = useStyles();

  const [favorited, setFavorited] = useState(email.favorited);

  const handleFavorite = () => {
    setFavorited(favorited => !favorited);
  };

  return (
    <ListItem
      {...rest}
      className={clsx(
        classes.root,
        {
          [classes.new]: !email.seen,
          [classes.selected]: selected
        },
        className
      )}
      divider
    >
      <Checkbox
        checked={selected}
        className={classes.checkbox}
        color="primary"
        onChange={onToggle}
      />
      <Tooltip title="Add to favs">
        <IconButton
          className={classes.favoriteButton}
          onClick={handleFavorite}
        >
          {favorited ? (
            <StarIcon
              className={clsx(classes.starIcon, classes.starFilledIcon)}
            />
          ) : (
            <StarBorderIcon className={classes.starIcon} />
          )}
        </IconButton>
      </Tooltip>
      <div
        className={classes.details}
        onClick={onOpen}
      >
        <Avatar
          className={classes.avatar}
          src={email.receiver.avatar}
        >
          {getInitials(email.receiver.name)}
        </Avatar>
        <div className={classes.content}>
          <Typography className={classes.name}>
            {email.receiver.name}
          </Typography>
          <Typography className={classes.subject}>{email.subject}</Typography>
          <Typography className={classes.message}>
            <span className={classes.separator}>-</span>
            {email.message}
          </Typography>
          <div className={classes.labels}>
            {email.labels.map((label, i) => (
              <Label
                color={label.color}
                key={i}
              >
                {label.text}
              </Label>
            ))}
          </div>
          <Typography
            className={classes.date}
            variant="body2"
          >
            {moment(email.created_at).format('DD MMM YYYY')}
          </Typography>
        </div>
      </div>
    </ListItem>
  );
};

EmailItem.propTypes = {
  className: PropTypes.string,
  email: PropTypes.object.isRequired,
  onOpen: PropTypes.func,
  onToggle: PropTypes.func,
  selected: PropTypes.bool.isRequired
};

export default EmailItem;
