import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import clsx from 'clsx';
import moment from 'moment';
import PropTypes from 'prop-types';
import PerfectScrollbar from 'react-perfect-scrollbar';
import { makeStyles } from '@material-ui/styles';
import { useSelector } from 'react-redux';

import {
  Button,
  Card,
  CardHeader,
  CardContent,
  Divider,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  colors
} from '@material-ui/core';

import axios from 'utils/axios';
import { Label, GenericMoreButton } from 'components';

const useStyles = makeStyles(() => ({
  root: {},
  content: {
    padding: 0
  },
  inner: {
    minWidth: 1150
  }
}));

const Invoices = props => {
  const { className, ...rest } = props;

  const classes = useStyles();
  const [invoices, setInvoices] = useState([]);

  useEffect(() => {
    let mounted = true;

    const fetchInvoices = () => {
      axios.get('/api/management/customers/1/invoices').then(response => {
        if (mounted) {
          setInvoices(response.data.invoices);
        }
      });
    };

    fetchInvoices();

    return () => {
      mounted = false;
    };
  }, []);


  const selectedUserVisits  = useSelector((state) => ({ ...state.accessLevel.selectedUserVisits }));
  //console.log('The selected user visits 1', selectedUserVisits);
  date1
  const currentDate =  Date.now();
  var admission = moment('{date1}', 'DD-MM-YYYY');
  var discharge = moment('{date2}', 'DD-MM-YYYY');
  discharge.diff(admission, 'days');
  console.log('The admission ', discharge.diff(admission, 'days'));


  useEffect(()=> {
    console.log('The selected user visits 33 ', currentDate);
   // const visitName = 

  },[])

  const statusColors = {
    0: colors.orange[600],
    false: colors.green[600],
    rejected: colors.red[600]
  };

  return (
    <div
      {...rest}
      className={clsx(classes.root, className)}
    >
      <Card>
        <CardHeader
          action={<GenericMoreButton />}
          title="Customer invoices"
        />
        <Divider />
        <CardContent className={classes.content}>
          <PerfectScrollbar>
            <div className={classes.inner}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>Call Date</TableCell>
                    <TableCell>Days to call</TableCell>
                    <TableCell>Total</TableCell>
                    <TableCell>Status</TableCell>
                    <TableCell align="right">Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {selectedUserVisits.phoneCalls.map(visitCalls => (
                    <TableRow key={visitCalls.id}>
                      <TableCell>
                        {selectedUserVisits.visitName} | Call
                      </TableCell>
                      <TableCell>{moment.unix(visitCalls.visit_date/1000).format('DD-MM-YYYY')}</TableCell>
                      <TableCell>
                        date
                      </TableCell>
                      <TableCell>
                        89
                      </TableCell>
                      <TableCell>
                        <Label
                          color={statusColors['rejected']}
                          variant="outlined"
                        >
                          missed
                        </Label>
                      </TableCell>
                      <TableCell align="right">
                        <Button
                          color="primary"
                          component={RouterLink}
                          size="small"
                          to={'/management/invoices/1'}
                          variant="outlined"
                        >
                          View
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          </PerfectScrollbar>
        </CardContent>
      </Card>
    </div>
  );
};

Invoices.propTypes = {
  className: PropTypes.string
};

export default Invoices;
