import { makeStyles } from '@material-ui/styles';
import { Page, SearchBar } from 'components';

import { FETCH_USERS } from '../../graphql/queries/app-queries'
import  {apiFetchData } from 'store/api-actions';





import { Header,ListUser } from './components'
import { async } from 'validate.js';

const useStyles = makeStyles(theme => ({
  root: {
    padding: theme.spacing(3)
  },
  results: {
    marginTop: theme.spacing(3)
  }
}));


function ListItems() {




  const classes = useStyles();
  const handleFilter = () => {};
  const handleSearch = () => {};


  const [customers, setCustomers] = useState([]);
  const [usersData, setUsers] = useState([]);
  const [data1, setdata] = useState([]);

  const fetchUsers = async() =>{
    await apiFetchData(FETCH_USERS,'None').then(user =>{
      console.log('FETCH_USERS 1  ',user.data.getUsers)
      setUsers(user.data.getUsers);

    });//

  }

  useEffect(() =>{
    fetchUsers();
  },[])





  return (
    <Page
      className={classes.root}
      title="List of all Users"
    >
      {/*  <Header /> */}

      <SearchBar
        onSearch={handleSearch}
      />
      { usersData.user ? (
        <ListUser
          className={classes.results}
          loadUsers={usersData}
        />
      ) : <p></p>} 

    </Page>
  )




}

export default ListItems
