import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';

import axios from 'utils/axios';
import { CustomerInfo, Invoices, SendEmails, OtherActions } from './components';
import {useParams } from 'react-router-dom';

import { FETCH_USERS } from '../../../../graphql/queries/app-queries'
import  {apiFetchData } from 'store/api-actions';



const useStyles = makeStyles(() => ({
  root: {}
}));

const Summary = props => {
  const { className, ...rest } = props;
  const [user, setUsers] = useState([]);


  const classes = useStyles();
  const [customer, setCustomer] = useState();
  let { id } = useParams();

  const userId = id;



  const fetchUsers = async(selectedUserId) =>{

    await apiFetchData(FETCH_USERS,selectedUserId).then(user =>{
      setUsers(user.data.getUsers);
    });//

  }




  useEffect(() => {
    let mounted = true;


    const fetchCustomer = () => {
      axios.get('/api/management/customers/1/summary').then(response => {
        if (mounted) {
          setCustomer(response.data.summary);

          // console.log( ' The params ============> response.data.summary ', response.data.summary)
          //fetchUsers(id); 
        }
      });
    };
    fetchUsers(id);  
    fetchCustomer();




  }, []);

  if (!customer) {
    return null;
  }

  return (
    <Grid
      {...rest}
      className={clsx(classes.root, className)}
      container
      spacing={3}
    >
      <Grid
        item
        lg={4}
        md={6}
        xl={3}
        xs={12}
      >
        {user.length > 0  && <CustomerInfo user={user} />}

      </Grid>
      {/*       <Grid
        item
        lg={4}
        md={6}
        xl={3}
        xs={12}
      >
        <Invoices customer={customer} />
      </Grid> */}
      <Grid
        item
        lg={6}
        md={8}
        xl={8}
        xs={12}
      >
        <SendEmails customer={customer} />
      </Grid>
      {/*       <Grid
        item
        lg={4}
        md={6}
        xl={3}
        xs={12}
      >
        <OtherActions />
      </Grid> */}
    </Grid>
  );
};

Summary.propTypes = {
  className: PropTypes.string
};

export default Summary;
