import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/styles';
import { Page, SearchBar } from 'components';
import axios from 'utils/axios';

import { FETCH_USERS } from '../../graphql/queries/users'
import  {fetchAccessLevels } from 'store/access-level-actions';





import { Header,ListUser } from './components'
import { async } from 'validate.js';

const useStyles = makeStyles(theme => ({
  root: {
    padding: theme.spacing(3)
  },
  results: {
    marginTop: theme.spacing(3)
  }
}));


function ListItems() {




  const classes = useStyles();
  const handleFilter = () => {};
  const handleSearch = () => {};


  const [customers, setCustomers] = useState([]);
  const [users, setUsers] = useState([]);
  const [data1, setdata] = useState([]);

  const fetchUsers = async() =>{
    await fetchAccessLevels(FETCH_USERS).then(users =>{
      setUsers(users.data.getUsers);
    });//

  }

  useEffect(() => {
    let mounted = true;

    const fetchCustomers = () => {
      axios.get('/api/management/customers').then(response => {
        if (mounted) {
          setCustomers(response.data.customers);
        }
      });
    };


    fetchCustomers();

    return () => {
      mounted = false;
    };
  }, []);

  useEffect(() => {
    let mounted = true;

    fetchUsers();    

    return () => {
      mounted = false;
    };
  }, []);





  return (
    <Page
      className={classes.root}
      title="List of all Users"
    >
      {/*  <Header /> */}
      <SearchBar
        onSearch={handleSearch}
      />
      { customers && (
        <ListUser
          className={classes.results}
          customers={customers}
        />
      )} 

    </Page>
  )
}

export default ListItems
