import gql from 'graphql-tag';

export const CREATE_USER = gql`
mutation createUser($firstName: String!, $lastName: String, $documentNumber: String,
     $documentType: String, $email: String, $address: String, $phone: String
     , $organizationId: String, $role: String){
     createUser(firstName:$lastName,lastName:$lastName, documentNumber:$documentNumber,documentType:$documentType,          
          email:$email, address:$address, phone:$phone, organizationId:$organizationId, role:$role
          ){ 
          firstName
          lastName
          documentNumber
          documentType
          email
          address
          phone
          organizationId
          role
     }
     }
`

//  login mutation
export const LOGIN = gql`
  mutation login($username: String!, $password: String!, $otp: Int!) {
    login(username: $username, password: $password, otp: $otp) {
      token
      refreshToken
      payload
      refreshExpiresIn
    }
  }
`;

export const  REGISTER_USER2 = gql`

mutation register( $username: String!,  $email:String!,  $password:String!,  $confirmPassword:String!){
  register(registerInput: 
  {username: $username, email: $email, password: $password, confirmPassword: $confirmPassword})
  {
    username email id, token
  }
}

`
