import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import moment from 'moment';
import { makeStyles } from '@material-ui/styles';
import {
  Card,
  CardHeader,
  CardContent,
  TextField,
  Button,
  Divider,
  Table,
  TableBody,
  TableRow,
  TableCell,
  ListItemSecondaryAction,
  Tooltip,
  IconButton
} from '@material-ui/core';
import MaiIcon from '@material-ui/icons/MailOutline';
import ArrowForwardIcon from '@material-ui/icons/ArrowForwardOutlined';
import {  EnrollInProgram } from './components';

import { FETCH_VISITS , FETCH_VISITS_SERVICES} from '../../../../../../graphql/queries/app-queries';
import  {apiFetchData } from 'store/api-actions';




const useStyles = makeStyles(theme => ({
  root: {},
  content: {
    padding: 0
  },
  actions: {
    flexDirection: 'column',
    alignItems: 'flex-start',
    '& > * + *': {
      marginLeft: 0
    }
  },
  buttonIcon: {
    marginRight: theme.spacing(1)
  }
}));

const VisitServices = props => {
  const { customer, className, ...rest } = props;
  const [visits, setVisits] = useState([]);
  const [selectedVisit, setSelectedVisit] = useState([]);
  const [visitServices, setVisitServices] = useState([]);


  const classes = useStyles();

  const options = [
    'Resend last invoice',
    'Send password reset',
    'Send verification'
  ];

  const fetchVisits = async () => {
    await apiFetchData(FETCH_VISITS).then(res =>{  
      console.log('Get Visits', res.data)
      setVisits(res.data.getVisits) 

    })
  }


  const fetchVisitServices = async () => {
    await apiFetchData(FETCH_VISITS_SERVICES).then(res =>{  
      console.log('visits service', res.data.getVisitServices)
      setVisitServices(res.data.getVisitServices) 

    })
  }


  useEffect(() =>{
    fetchVisits();
    fetchVisitServices()

  },[]);


  const [option, setOption] = useState(options[0]);



  const [openEnrol, setOpenEnroll] = useState(false);




  const handleEditOpen = (event,data) => {
    console.log(data ,'Selected visit ',event)
    setSelectedVisit(data)
    setOpenEnroll(true);
  };


  const handleEnrollClose = (data) => {
    console.log("Closing add services modal...", data);
    setOpenEnroll(false);
  };
  const handleChange = event => {
    event.persist();

    setOption(event.target.value);
  };

  return (
    <Card
      {...rest}
      className={clsx(classes.root, className)}
    >
      <CardHeader title="Study Visits" />
      <Divider />
      <CardContent className={classes.content}>
        <TextField
          fullWidth
          name="option"
          onChange={handleChange}
          select
          // eslint-disable-next-line react/jsx-sort-props
          SelectProps={{ native: true }}
          value={option}
          variant="outlined"
        >
          {options.map(option => (
            <option
              key={option}
              value={option}
            >
              {option}
            </option>
          ))}
        </TextField>

        <Table className={classes.table}>
          <TableBody>
            {visits.map(visit => (
              <TableRow key={visit.id}>

                <TableCell className={classes.cell}>
                  {visit.visitName} 
                </TableCell>
                <TableCell className={classes.cell}>
                  {visit.description}
                </TableCell>
                <TableCell className={classes.cell}>
                  Window({visit.visitNumber})
                </TableCell>

                <TableCell className={classes.cell}>
                  <Tooltip title="View">
                    <IconButton
                      onClick={event =>
                        handleEditOpen(event, visit)
                      }
                      edge="end"
                      size="small"
                    >
                      <ArrowForwardIcon />
                    </IconButton>
                  </Tooltip>
                </TableCell>

              </TableRow>
            ))}
          </TableBody>
        </Table>
      </CardContent>      

      <EnrollInProgram
        visit={selectedVisit}
        visitServices = {visitServices}
        onClose={handleEnrollClose}
        open={openEnrol}
        save
      />
    </Card>
  );
};

VisitServices.propTypes = {
  className: PropTypes.string,
  //customer: PropTypes.object.isRequired
};

export default VisitServices;
